import React, { useState } from 'react'
import LineChart from './LineChart';


const GetRadon = () => {

    const [sensor, setData] = React.useState([]);

    React.useEffect(() => {
        obtenerDatos()
    }, []);

    const obtenerDatos = async () => {
        const data = await fetch('/api/radon')
        const dataSensor = await data.json()
        setData(dataSensor)
    }

    const [sensorplot, setSensorData] = useState({
        labels: sensor.map((data) => data.ts),
        datasets: [{
            label: "data sensor",
            data: sensor.map((data) => data.value),
        }]
    })
}

export default GetRadon;
