import React, { useEffect, useState } from 'react'
import { Chart as ChartJS } from 'chart.js/auto'
import { Bar } from 'react-chartjs-2';


const GetBCMagee = () => {

    const [sensor, setData] = useState([]);


    useEffect(() => {
        async function obtenerDatos() {
            const response = await fetch('/api/bc-magee');
            const data = await response.json();
            setData(data);
        }

        obtenerDatos();
    }, []);

    const graphData = {
        labels : sensor.map((data) => data.Date.ts),
        datasets: [
            {
                label: "BC1",
                data: sensor.map((data) => data.BC1.value),
                backgroundColor: "#55b169",
            },
            {
                label: "BC2",
                data: sensor.map((data) => data.BC2.value),
                backgroundColor: "#cec845",
            },
            {
                label: "BC3",
                data: sensor.map((data) => data.BC3.value),
                backgroundColor: "#ff8f02",
            },
            {
                label: "BC4",
                data: sensor.map((data) => data.BC4.value),
                backgroundColor: "#4f5ccb",
            },
            {
                label: "BC5",
                data: sensor.map((data) => data.BC5.value),
                backgroundColor: "#fe1fa2",
            },
            {
                label: "BC6",
                data: sensor.map((data) => data.BC6.value),
                backgroundColor: "#F45B69",
            },
            {
                label: "BC7",
                data: sensor.map((data) => data.BC7.value),
                backgroundColor: "#34d6dd",
            }
        ]
    }

    const options = {
        responsive: true
    }


    return(
        <div>
            <Bar options={options} data={graphData} />
        </div>
    );
    
}

export default GetBCMagee;
